mapper.select:
select userpwd
from client_log
where userid = #{userid}

mapper.update:
update client_log
set username = #{username},
    userpwd  = #{userpwd}
where userid = #{userid}

mapper.select:
select userid
from client_log
where username = #{username,jdbcType=VARCHAR}

mapper.update:
update client_log
set userpwd = #{userpwd,jdbcType=VARCHAR}
where userid = #{userid,jdbcType=NUMERIC}
